namespace MSlwk\Otomoto\App\Test\Unit\Manufacturer\Scrapper;

use MSlwk\Otomoto\App\Manufacturer\Data\ManufacturerDTOArray;
use MSlwk\Otomoto\App\Manufacturer\Scrapper\ManufacturerHtmlScrapper;
use PHPUnit\Framework\TestCase;

/**
 * Class ManufacturerHtmlScrapperTest
 * @package MSlwk\Otomoto\App\Test\Unit\Manufacturer\Scrapper
 */
class ManufacturerHtmlScrapperTest extends TestCase
{
    /**
     * @test
     * @dataProvider htmlDataProvider
     * @param $html
     */
    public function testScrapperReturnsCorrectManufacturerDTOArray($html)
    {
        $scrapper = new ManufacturerHtmlScrapper();
        $manufacturerDTOArray = $scrapper->scrapManufacturers($html);

        $expectedType = ManufacturerDTOArray::class;
        $expectedCount = 112;
        $expectedNameOfFirstItem = 'Volkswagen';
        $this->assertInstanceOf($expectedType, $manufacturerDTOArray);
        $this->assertEquals($expectedCount, $manufacturerDTOArray->count());
        $this->assertEquals($expectedNameOfFirstItem, $manufacturerDTOArray->current()->getName());
    }

