    /**
     * @return void
     */
    protected function setUp()
    {
        $this->adapterFactory = $this->getMockBuilder(GETHandlerAdapterFactoryInterface::class)
            ->getMock();
        $this->GETAdapter = $this->getMockBuilder(GETHandlerInterface::class)
            ->getMock();
        $this->adapterFactory->expects($this->once())
            ->method('create')
            ->will($this->returnValue($this->GETAdapter));

        $this->urlPovider = $this->getMockBuilder(UrlProvider::class)
            ->disableOriginalConstructor()
            ->getMock();

        $this->manufacturerUrlSuffixProvider = $this->getMockBuilder(ManufacturerUrlSuffixProviderInterface::class)
            ->getMock();

        $this->modelUrlSuffixProvider = $this->getMockBuilder(ModelUrlSuffixProviderInterface::class)
            ->getMock();

        $this->pager = $this->getMockBuilder(PagerInterface::class)
            ->getMock();

        $this->modelOffersHtmlProvider = new ModelOffersHtmlProvider(
            $this->adapterFactory,
            $this->urlPovider,
            $this->manufacturerUrlSuffixProvider,
            $this->modelUrlSuffixProvider,
            $this->pager
        );
    }

